import React, {useState, useEffect} from 'react'
import axios from 'axios'
import { useNavigate, useParams } from 'react-router-dom'

import './adminDepartmentDataActions.css'

const AdminRegisterSignUp = () => {
    const [name, set_name] = useState('')
    const [nim, set_nim] = useState('')
    const [department, set_department] = useState('')
    const [batch, set_batch] = useState('')
    const [email, set_email] = useState('')
    const [password, set_password] = useState('')

    const navigate = useNavigate()

    const saveDepartment = async (e) => {
        e.preventDefault()
        try {
            await axios.post('http://localhost:8000/departments', {
                name,
                nim,
                department,
                batch,
                email,
                bronze_medal_count
            })
            navigate('/admin/department')
        } catch (error) {
            console.log(error)
        }
    }

    // const navigate = useNavigate()
    // const {id} = useParams()

    // useEffect(() => {
    //   getDepartmentById()
    // }, [])

    // const getDepartmentById = async () => {
    //   const response = await axios.get(`http://localhost:8000/departments/${id}`)
    //   set_dept_name(response.data.dept_name)
    //   set_dept_code(response.data.dept_code)
    //   set_dept_image(response.data.dept_image)
    //   set_gold_medal_count(response.data.gold_medal_count)
    //   set_silver_medal_count(response.data.silver_medal_count)
    //   set_bronze_medal_count(response.data.bronze_medal_count)
    // }

    const updateDepartment = async (e) => {
        e.preventDefault()
        try {
          await axios.post('http://localhost:8000/register', {
              name,
              nim,
              department,
              batch,
              email,
              password
          })
          navigate('/admin')
        } catch (error) {
          console.log(error)
        }
    }

  return (
    <div className='department-container' >
        <div className='spirit__heading'>
          <h1>Register</h1>
        </div>
        <form onSubmit={updateDepartment} className='department-form'>
            <div className='field'>
                <label className='label'>Nama</label>
                <div className='control'>
                    <input 
                        type="text" 
                        className='input'
                        value={name}
                        onChange={(e) => set_name(e.target.value)}
                        placeholder='Nama Lengkap'
                    />
                </div>
            </div>
            <div className='field'>
                <label className='label'>NIM</label>
                <div className='control'>
                    <input 
                        type="text" 
                        className='input'
                        value={nim}
                        onChange={(e) => set_nim(e.target.value)}
                        placeholder='NIM'
                    />
                </div>
            </div>
            <div className='field'>
                <label className='label'>Departemen</label>
                <div className='control'>
                    <input 
                        type="text" 
                        className='input'
                        value={department}
                        onChange={(e) => set_department(e.target.value)}
                        placeholder='Departemen'
                    />
                </div>
            </div>
            <div className='field'>
                <label className='label'>Angkatan</label>
                <div className='control'>
                    <input 
                        type="text" 
                        className='input'
                        value={batch}
                        onChange={(e) => set_batch(e.target.value)}
                        placeholder='Angkatan'
                    />
                </div>
            </div>
            <div className='field'>
                <label className='label'>Email</label>
                <div className='control'>
                    <input 
                        type="text" 
                        className='input'
                        value={email}
                        onChange={(e) => set_email(e.target.value)}
                        placeholder='Email'
                    />
                </div>
            </div>
            <div className='field'>
                <label className='label'>Password</label>
                <div className='control'>
                    <input 
                        type="password" 
                        className='input'
                        value={password}
                        onChange={(e) => set_password(e.target.value)}
                        placeholder='Password'
                    />
                </div>
            </div>
            <div className='field'>
                <div className='control'>
                    <button type='submit' className='department__add-button'>
                        DAFTAR
                    </button>
                </div>
            </div>
        </form>
    </div>
  )
}

export default AdminRegisterSignUp
