import axios from 'axios'
import { useNavigate, useParams } from 'react-router-dom'

import './adminDepartmentDataActions.css'

const AdminRegisterSignUp = () => {
    const [name, set_name] = useState('')
    const [nim, set_nim] = useState('')
    const [batch, set_batch] = useState('')
    const [email, set_email] = useState('')
    const [password, set_password] = useState('')

    // const navigate = useNavigate()
    // const {id} = useParams()

    // useEffect(() => {
    //   getDepartmentById()
    // }, [])

    // const getDepartmentById = async () => {
    //   const response = await axios.get(`http://localhost:8000/departments/${id}`)
    //   set_dept_name(response.data.dept_name)
    //   set_dept_code(response.data.dept_code)
    //   set_dept_image(response.data.dept_image)
    //   set_gold_medal_count(response.data.gold_medal_count)
    //   set_silver_medal_count(response.data.silver_medal_count)
    //   set_bronze_medal_count(response.data.bronze_medal_count)
    // }

    const updateDepartment = async (e) => {
        // e.preventDefault()
        // try {
        //   await axios.patch(`http://localhost:8000/departments/${id}`, {
        //       dept_name,
        //       dept_code,
        //       dept_image,
        //       gold_medal_count,
        //       silver_medal_count,
        //       bronze_medal_count
        //   })
        //   navigate('/admin/department')
        // } catch (error) {
        //   console.log(error)
        // }
    }

  return (
    <div className='department-container' >
        <div className='spirit__heading'>
          <h1>Register</h1>
        </div>
        <form onSubmit={updateDepartment} className='department-form'>
            <div className='field'>
                <label className='label'>Nama</label>
                <div className='control'>
                    <input 
                        type="text" 
                        className='input'
                        value={name}
                        onChange={(e) => set_name(e.target.value)}
                        placeholder='Nama Lengkap'
                    />
                </div>
            </div>
            <div className='field'>
                <label className='label'>NIM</label>
                <div className='control'>
                    <input 
                        type="text" 
                        className='input'
                        value={nim}
                        onChange={(e) => set_nim(e.target.value)}
                        placeholder='NIMn'
                    />
                </div>
            </div>
            <div className='field'>
                <label className='label'>Gambar Departemen</label>
                <div className='control'>
                    <input 
                        type="text" 
                        className='input'
                        value={dept_image}
                        onChange={(e) => set_dept_image(e.target.value)}
                        placeholder='Gambar Departemen'
                    />
                </div>
            </div>
            <div className='field'>
                <label className='label'>Jumlah Mendali Emas</label>
                <div className='control'>
                    <input 
                        type="text" 
                        className='input'
                        value={gold_medal_count}
                        onChange={(e) => set_gold_medal_count(e.target.value)}
                        placeholder='Jumlah Mendali Emas'
                    />
                </div>
            </div>
            <div className='field'>
                <label className='label'>Jumlah Mendali Perak</label>
                <div className='control'>
                    <input 
                        type="text" 
                        className='input'
                        value={silver_medal_count}
                        onChange={(e) => set_silver_medal_count(e.target.value)}
                        placeholder='Jumlah Mendali Perak'
                    />
                </div>
            </div>
            <div className='field'>
                <label className='label'>Jumlah Mendali Perunggu</label>
                <div className='control'>
                    <input 
                        type="text" 
                        className='input'
                        value={bronze_medal_count}
                        onChange={(e) => set_bronze_medal_count(e.target.value)}
                        placeholder='Jumlah Mendali Perunggu'
                    />
                </div>
            </div>
            <div className='field'>
                <div className='control'>
                    <button type='submit' className='department__add-button'>
                        UPDATE
                    </button>
                </div>
            </div>
        </form>
    </div>
  )
}

export default AdminRegisterSignUp
