import React, { useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import './medalTable.css'

import { Medals } from '../../components/index'
import { Department } from '../../data/Department'

import { getDepartments } from '../../features/departments/departmentSlice'

const MedalTable = () => {
  const dispatch = useDispatch()

  // const { departments, isLoading, isError, message } = useSelector(
  //   (state) => state.departments
  // )

  // const { departments, isLoading, isError, message } = useSelector(
  //   (state) => state.departments
  // )

  useEffect(() => {
    if (isError) {
      console.log(message)
    }

    if (!user) {
      navigate('/login')
    }

    dispatch(getDepartments())

    return () => {
      dispatch(reset())
    }
  }, [user, navigate, isError, message, dispatch])

  return (
    <div className='spirit__content section__padding' id='medalTable'>
        <div className='spirit__heading'>
          <h1>KLASEMEN</h1>
        </div>
        <div className='spirit__klasemen_medalTable-result'>
            <div className='spirit__klasemen_medalTable-heading'>
                <div className='spirit__klasemen-departments_container'>
                    <p>Department</p>
                </div>
                <div className='spirit__klasemen-medals_container'>
                    <p>Gold</p>
                    <p>Silver</p>
                    <p>Bronze</p>
                    <p>Total</p>
                </div>
            </div>
            {Department.sort((a, b) => (b.goldMedal - a.goldMedal)).map((data) => (
                <Medals data={data} key={data.id} />
            ))}
        </div>
    </div>
  )
}

export default MedalTable
