import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { useParams } from 'react-router-dom'
import './match.css'

import { MatchResult, MatchSquad, Comment, CommentList } from '../../components/index'

const MatchDetail = () => {
  const params = useParams()

  const [matchs, setMatchs] = useState([])

  useEffect(() => {
    getMatchs()
  }, [])

  const getMatchs = async () => {
    const response = await axios.get(`http://localhost:8000/api/matchs/${params.id}`)
    setMatchs(response.data)
  }

  console.log(matchs.comments_list[0])

  return (
    <div className='section__padding'>
      <MatchResult data={matchs}/>
      <MatchSquad data={matchs}/>
      <Comment id={params.id}/>
      {matchs.map((params) => (
        <p><param name="" value="" /></p>
      ))}
      <CommentList data={matchs}/>
    </div>
  )
}

export default MatchDetail
