import React, {useState, useEffect} from 'react'
import axios from 'axios'
import { useNavigate, useParams } from 'react-router-dom'

import './adminDepartmentDataActions.css'

const AdminLoginSignIn = () => {
    const [email, set_email] = useState('')
    const [password, set_password] = useState('')

    const navigate = useNavigate()

    const loginAdmin = async (e) => {
        e.preventDefault()
        try {
            await axios.post('http://localhost:8000/login', {
                email,
                password
            })

            const admin = await Admin.findOne({
                email: email, 
                password: password
            })

            if(admin) {
                return res.json({ status: 'ok', admin: true })
            }
            navigate('/admin')
        } catch (error) {
            console.log({message: error.message, error:'Email atau Password salah'})
        }
    }

  return (
    <div className='department-container' >
        <div className='spirit__heading'>
          <h1>MASUK</h1>
        </div>
        <form onSubmit={loginAdmin} className='department-form'>
            <div className='field'>
                <label className='label'>Email</label>
                <div className='control'>
                    <input 
                        type="text" 
                        className='input'
                        value={email}
                        onChange={(e) => set_email(e.target.value)}
                        placeholder='Email'
                    />
                </div>
            </div>
            <div className='field'>
                <label className='label'>Password</label>
                <div className='control'>
                    <input 
                        type="password" 
                        className='input'
                        value={password}
                        onChange={(e) => set_password(e.target.value)}
                        placeholder='Password'
                    />
                </div>
            </div>
            <div className='field'>
                <div className='control'>
                    <button type='submit' className='department__add-button'>
                        DAFTAR
                    </button>
                </div>
            </div>
        </form>
    </div>
  )
}

export default AdminLoginSignIn
