import React from 'react'
import './commentList.css'

const CommentList = ({data}) => {
  return (
    <div>
        {data.comments_list?.map((comment) => (
            // <div>
            //     {comment.username}
            //     <br/>
            //     {comment.comment}
            // </div>
            // <li className="comment-item">
              <div className="comment-box">
                <div className="comment-box__head">
                  <section className="head__name">{comment.username}</section>
                  <section className="head__datetime">{comment._id}</section>
                </div>
                <div className="comment-box__body">{comment.comment}</div>
              </div>
            // </li>
        ))}
    </div>
  )
}

export default CommentList
