import React, {useState, use} from 'react'
import './tableDepartment.css'

import axios from 'axios'
import {Link} from 'react-router-dom'

const ActionDepartment = ({data}) => {

  const [departments, setDepartment] = useState([])

  useEffect(() => {
    getDepartments()
  }, [])

  const getDepartments = async () => {
    const response = await axios.get('http://localhost:8000/departments')
    setDepartment(response.data)
  }

  const deleteDepartment = async (id) => {
    try {
      await axios.delete(`http://localhost:8000/departments/${id}`)
      getDepartments()
    } catch (error) {
      console.log(error)
    }
  }

  return (
    <div className='admin__department'>
        <div className='admin__department-button'>
            <div className='admin__edit-button'>
              <Link to={`/admin/department/edit/${data._id}`} key={data._id}>
                <button>Edit</button>
              </Link>
            </div>
            <div  className='admin__delete-button'>
                <button>Delete</button>
            </div>     
        </div>
    </div>
  )
}

export default ActionDepartment
