import React, { useState } from 'react'

import './kompetisi.css'
import { Article } from '../../components'
import olahragaImage from '../../assets/kompetisi/kompetisi-olahraga.jpeg'
import seniImage from '../../assets/kompetisi/kompetisi-seni.jpeg'

const kompetisiData = [
  {
    type: 'Olahraga',
    text: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.  Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.',
    kompetisiImage: olahragaImage
  },
  {
    type: 'Seni',
    text: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.  Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.',
    kompetisiImage: seniImage
  }
]

const cardData = [
  {
    num: '1'
  },
  {
    num: '2'
  },
  {
    num: '3'
  },
  {
    num: '4'
  },
  {
    num: '5'
  },
]

const Kompetisi = () => {
  const [slideIndex, setSlideIndex] = useState(1)

  const moveDot = index => {
      setSlideIndex(index)
  }

  const [slideIndex2, setSlideIndex2] = useState(1)

  const moveDot2 = index2 => {
      setSlideIndex2(index2)
  }

  let box = document.getElementById('.container1')

  const moveDot3 = () => {
    let height = box.clientHeight
    box.scrollTop = box.scrollTop - height
    console.log(height)
  }

  return (
    <div className='spirit__kompetisi section__padding' id='kompetisi'>
      <div className='spirit__kompetisi-heading'>
        <h1>KOMPETISI</h1>
      </div>

      <div className='spirit__kompetisi_slider-container'>
        {kompetisiData.map((item, index) => {
          return (
            <div key={item.type} className={slideIndex === index + 1 ? 'kompetisi-slide active-anim' : 'kompetisi-slide'}>
              <div className='spirit__article-heading'>
                <h2>{item.type}</h2>
              </div>
              <div className='spirit__kompetisi-article'>
                <Article text={item.text} contentImage={item.kompetisiImage} />
              </div>
            </div>
          )
        })}

        <div className="container-dots">
          {Array.from({length: 2}).map((item, index) => (
              <div 
                  onClick={() => moveDot(index + 1)}
                  className={slideIndex === index + 1 ? "dot active" : "dot"}
              ></div>
          ))}
        </div>
      </div>

      {/* tes */}

      <div>
        <div className='container1'>
          {cardData.map((item, index) => {
            return (
              <div className='card'>
                <button>{item.num}</button>
              </div>
            )
          })}
        </div>

        <div className="container-dots2">
          {Array.from({length: 5}).map((item, index2) => (
              <div 
                  onClick={() => moveDot2(index2 + 1)}
                  className={slideIndex2 === index2 + 1 ? "dot2 active2" : "dot2"}
              ></div>
          ))}
        </div>
      </div>

    </div>
  )
}

export default Kompetisi
