import React, { useState } from "react";
import axios from "axios";

import "./commentInput.css";

const CourseInput = ({props}) => {
  const [showForm, setShowForm] = useState(false);
  const [username, set_username] = useState("");
  const [comment, set_comment] = useState("");
  const [match_id, set_match_id] = useState(props)

  const commentSubmitHandler = async (event) => {
    event.preventDefault();
    try {
        await axios.post('http://localhost:8000/api/students', {
            username,
            comment,
            match_id
        })
        window.location.reload(false)
    } catch (error) {
      console.log(error);
    }
  };

  let form = (
    <form onSubmit={commentSubmitHandler} className="spirit-form">
      <div className="field">
        <label className="label">Siapa kamu?</label>
        <div className="control">
          <input
            type="text"
            className="input"
            value={username}
            onChange={(e) => set_username(e.target.value)}
          />
        </div>
      </div>
      <div className="field">
        <label className="label">Isi komentar</label>
        <div className="control">
          <textarea
            type="text"
            className="input"
            value={comment}
            onChange={(e) => set_comment(e.target.value)}
          />
        </div>
      </div>
      <div className="control">
        <button
          type="submit"
          className="spirit__add-button"
          onClick={() => setShowForm(false)}
        >
          Post
        </button>
      </div>
    </form>
  );

  if (showForm === false) {
    form = <div></div>;
  }

  return (
    <div>
      <div className="comment-button" onClick={() => setShowForm(true)}>
        <div>Tulis Komentar atau Dukungan</div>
      </div>
      {form}
    </div>
  );
};

export default CourseInput;
