import React from 'react'

import Slider from 'react-slick'

import './kontingenDepartment.css'
import 'slick-carousel/slick/slick.css'
import 'slick-carousel/slick/slick-theme.css'

import { g1, g2, g3, g4, g5, g6, g7, g8 } from '../../components/department/imports'

const departmentData = [
    {
      departmentImage: g1,
      departmentKey: 'G1',
      departmentName: 'Statistika'
    },
    {
      departmentImage: g2,
      departmentKey: 'G2',
      departmentName: 'GFM'
    },
    {
      departmentImage: g3,
      departmentKey: 'G3',
      departmentName: 'Biologi'
    },
    {
      departmentImage: g4,
      departmentKey: 'G4',
      departmentName: 'Kimia'
    },
    {
      departmentImage: g5,
      departmentKey: 'G5',
      departmentName: 'Matematika'
    },
    {
      departmentImage: g6,
      departmentKey: 'G6',
      departmentName: 'Ilmu Komputer'
    },
    {
      departmentImage: g7,
      departmentKey: 'G7',
      departmentName: 'Fisika'
    },
    {
      departmentImage: g8,
      departmentKey: 'G8',
      departmentName: 'Biokimia'
    },
]

const KontingenDepartment = () => {
    const [kompetisiTag, setKompetisiTag] = useState('')

    useEffect(() => {
      getKompetisiTag(kompetisiTag)
    }, [getKompetisiTag, kompetisiTag])

    let settings = {
      infinite: true,
      speed: 500,
      slidesToShow: 3,
      slidesToScroll: 3,
      cssEase: 'linear'
    }

    return (
      <div className='spirit__content section__padding' id='olahraga'>
          <div className='spirit__heading'>
              <h1>OLAHRAGA</h1>
          </div>
          <div className='spirit__kompetisi-container'>
            <Slider {...settings}>
              {olahragaData.map((item, index) => (
                <KometisiList 
                  kompetisiImage={item.olahragaImage} 
                  kompetisiName={item.olahragaName} 
                  kompetisiPropTag={setKompetisiTag}
                  key={item.olahragaName}
                />
              ))}
            </Slider>
          </div>
      </div>
    )
}

export default KontingenDepartment
