import React, {useState, useEffect} from 'react'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'

import './comment.css'

const SportDataAdd = ({id}) => {
    const [username, set_username] = useState('')
    const [comment, set_comment] = useState('')
    const [match_id, set_match_id] = useState(id)

    const navigate = useNavigate()

    const onSubmit = async (e) => {
        e.preventDefault()
        try {
            await axios.post('http://localhost:8000/api/students', {
                username,
                comment,
                match_id
            })
            window.location.reload(false)
        } catch (error) {
            console.log(error)
        }
    }

    const [data, setData] = useState([]);

    useEffect(() => {
        getData();
    }, []);

    const getData = async () => {
        const response = await axios.get("http://localhost:8000/api/sports");
        setData(response.data);
      };

  return (
    <div className='spirit-container'>
        <div className='spirit__heading'>
          <h1>KOMENTAR</h1>
        </div>
        <form onSubmit={onSubmit} className='spirit-form'>
            <div className='field'>
                <label className='label'>Username</label>
                <div className='control'>
                    <input 
                        type="text" 
                        className='input'
                        value={username}
                        onChange={(e) => set_username(e.target.value)}
                        placeholder='Username'
                    />
                </div>
            </div>
            <div className='field'>
                <label className='label'>Komentar</label>
                <div className='control'>
                    <input 
                        type="text" 
                        className='input'
                        value={comment}
                        onChange={(e) => set_comment(e.target.value)}
                        placeholder='Komentar'
                    />
                </div>
            </div>
            <div className='field'>
                <div className='control'>
                    <button type='submit' className='spirit__add-button'>
                        Tambah
                    </button>
                </div>
            </div>
        </form>

        {data.map((params) => {
            <p>pa</p>
        })}
    </div>
  )
}

export default SportDataAdd
