import React, { useState, useEffect } from 'react'
import axios from 'axios'
import './jadwalSeni.css'

import { KompetisiSeni } from '../index'
import { SeniJadwal } from '../../components/index'

const contest = [
  {
    art_name: 'KREASI TARI DAERAH',
    art_type: 'Performed Art',
    location: 'Auditorium FMIPA',
    day: 'Senin',
    date: '24 Oktober 2022',
    time: '19:30',
    is_finished: false,
    artist: []
  },
  {
      art_name: 'STANUD-UP COMEDY',
      art_type: 'Performed Art',
      location: 'Auditorium FMIPA',
      day: 'Rabu',
      date: '26 Oktober 2022',
      time: '16:00',
      is_finished: false,
      artist: []
  },
  {
      art_name: 'TULIS CERPEN',
      art_type: 'Submisive Art',
      location: 'Auditorium FMIPA',
      day: 'Rabu',
      date: '26 Oktober 2022',
      time: '15:00',
      is_finished: false,
      artist: []
  }
]

const JadwalSeni = () => {

  const [contests, setContests] = useState([])

  useEffect(() => {
    getContests()
  }, [])

  const getContests = async () => {
    const response = await axios.get('http://localhost:8000/api/contests')
    setContests(response.data)
  }

  const [listTag, setListTag] = useState({listName: ''})

  const jadwalFiltered = (
    contest.filter(tag => {
      return tag.art_name.includes(listTag.listName)
    })
  )

  return (
      <>
          < KompetisiSeni getListTag={setListTag} />
          < SeniJadwal jadwalData={jadwalFiltered} />
      </>
  )
}

export default JadwalSeni
