import React, {useState, useEffect} from 'react'
import axios from 'axios'
import { useNavigate, useParams } from 'react-router-dom'

import './spiritDataActions.css'

const DepartmentDataEdit = () => {
    const [sport_name, set_sport_name] = useState('')
    const [sport_image, set_sport_image] = useState('')

    const navigate = useNavigate()
    const {id} = useParams()

    useEffect(() => {
      getSportById()
    }, [])

    const getSportById = async () => {
      const response = await axios.get(`http://localhost:8000/api/sports/${id}`)
      set_sport_name(response.data.sport_name)
      set_sport_image(response.data.sport_image)
    }

    const updateDepartment = async (e) => {
        e.preventDefault()
        try {
          await axios.patch(`http://localhost:8000/api/sports/${id}`, {
              sport_name,
              sport_image
          })
          navigate('/admin/department')
        } catch (error) {
          console.log(error)
        }
    }

  return (
    <div className='department-container' >
        <div className='spirit__heading'>
          <h1>UPDATE DEPARTEMEN</h1>
        </div>
        <form onSubmit={updateDepartment} className='department-form'>
            <div className='field'>
                <label className='label'>Nama Departemen</label>
                <div className='control'>
                    <input 
                        type="text" 
                        className='input'
                        value={sport_name}
                        onChange={(e) => set_sport_name(e.target.value)}
                        placeholder='Nama Departemen'
                    />
                </div>
            </div>
            <div className='field'>
                <label className='label'>Gambar Departemen</label>
                <div className='control'>
                    <input 
                        type="text" 
                        className='input'
                        value={sport_image}
                        onChange={(e) => set_sport_image(e.target.value)}
                        placeholder='Gambar Departemen'
                    />
                </div>
            </div>
            <div className='field'>
                <div className='control'>
                    <button type='submit' className='department__add-button'>
                        UPDATE
                    </button>
                </div>
            </div>
        </form>
    </div>
  )
}

export default DepartmentDataEdit
