import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { TableDepartment} from '../../components/index'
import './adminDepartmentData.css'

const AdminDepartmentData = () => {

  const [departments, setDepartment] = useState([])

  useEffect(() => {
    getDepartments()
  }, [])

  const getDepartments = async () => {
    const response = await axios.get('http://localhost:8000/departments')
    setDepartment(response.data)
  }

  return (
    <div className='data-container'> 
      <div className='admin__department-data'>
        {departments.map((data => (
          <TableDepartment data={data}/>
        )))}
      </div>
      {departments.map((data => (
        <div className='admin__department-button'>
            <div className='admin__edit-button'>
                <button>Edit</button>
            </div>
            <div  className='admin__delete-button'>
                <button>Delete</button>
            </div>     
        </div>
      )))}
    </div>
  )
}

export default AdminDepartmentData
