import React, { useState, useEffect } from 'react'
import axios from 'axios'
import './kontingenContainer.css'

import { KontingenDeparment, KontingenParticipant } from '../index'

const KontingenContainer = () => {

    const artists = [
        {
            name: 'seni',
            nim: 'seni',
            image: "https://i.ibb.co/27NmvV1/spirit-participant.png",
            department: 'GFM',
            batch: 'seni',
            artist_in: 'seni'
        },
        {
            name: 'seni',
            nim: 'seni',
            image: "https://i.ibb.co/27NmvV1/spirit-participant.png",
            department: 'GFM',
            batch: 'seni',
            artist_in: 'seni'
        }
    ]

    const [athletes, setAthletes] = useState([])
    const [participants, setParticipants] = useState([])


    useEffect(() => {
        getParticipants()
    }, [])

    const getParticipants = async () => {
        const response = await axios.get('http://localhost:8000/api/athletes')
        setAthletes(response.data)
        setParticipants(response.data)
        setParticipants(curr => [...curr, ...artists])
    }

    const [listTag, setListTag] = useState({listName: ''})

    const kontingenFiltered = (
        participants.filter(tag => {
        return tag.department.includes(listTag.listName)
        })
    )

    participants.map(x => {
        const type = x.sport_team
        if(type === undefined) {
            console.log(x.artist_in)
        }
        else console.log(type)
    })

    return (
        <>
            < KontingenDeparment getListTag={setListTag} />
            < KontingenParticipant participantData={kontingenFiltered}/>
        </>
    )
}

export default KontingenContainer
