import React, {useState} from 'react'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'

import './spiritDataActions.css'

const ArticleDataAdd = () => {
    const [title, set_title] = useState('')
    const [author, set_author] = useState('')
    const [body, set_body] = useState('')
    const [cover_image, set_cover_image] = useState('')
    const [is_published, set_is_published] = useState('')
    const [created_date, set_created_date] = useState('')
    const [published_date, set_published_date] = useState('')

    const navigate = useNavigate()

    const onSubmit = async (e) => {
        e.preventDefault()
        try {
            await axios.post('http://localhost:8000/api/articles', {
                title,
                author,
                body,
                cover_image,
                is_published,
                created_date,
                published_date
            })
            navigate('/admin/article')
        } catch (error) {
            console.log(error)
        }
    }

  return (
    <div className='spirit-container'>
        <div className='spirit__heading'>
          <h1>TAMBAH ARTIKEL</h1>
        </div>
        <form onSubmit={onSubmit} className='spirit-form'>
            <div className='field'>
                <label className='label'>Judul</label>
                <div className='control'>
                    <input 
                        type="text" 
                        className='input'
                        value={title}
                        onChange={(e) => set_title(e.target.value)}
                        placeholder='Judul'
                    />
                </div>
            </div>
            <div className='field'>
                <label className='label'>Gambar Olahraga</label>
                <div className='control'>
                    <input 
                        type="text" 
                        className='input'
                        value={author}
                        onChange={(e) => set_author(e.target.value)}
                        placeholder='Gambar Olahraga'
                    />
                </div>
            </div>
            <div className='field'>
                <div className='control'>
                    <button type='submit' className='spirit__add-button'>
                        Tambah
                    </button>
                </div>
            </div>
        </form>
    </div>
  )
}

export default ArticleDataAdd
