import React, {useState} from 'react'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'

import './spiritDataActions.css'

const SportDataAdd = () => {
    const [sport_name, set_sport_name] = useState('')
    const [sport_image, set_sport_image] = useState('')

    const navigate = useNavigate()

    const saveSport = async (e) => {
        e.preventDefault()
        try {
            await axios.post('http://localhost:8000/api/sports', {
                sport_name,
                sport_image,
            })
            navigate('/admin/sport')
        } catch (error) {
            console.log(error)
        }
    }

  return (
    <div className='spiritcontainer'>
        <div className='spirit__heading'>
          <h1>TAMBAH OLAHRAGA</h1>
        </div>
        <form onSubmit={saveSport} className='spiritform'>
            <div className='field'>
                <label className='label'>Nama Olahrga</label>
                <div className='control'>
                    <input 
                        type="text" 
                        className='input'
                        value={sport_name}
                        onChange={(e) => set_sport_name(e.target.value)}
                        placeholder='Nama Olahraga'
                    />
                </div>
            </div>
            <div className='field'>
                <label className='label'>Gambar Olahraga</label>
                <div className='control'>
                    <input 
                        type="text" 
                        className='input'
                        value={sport_image}
                        onChange={(e) => set_sport_image(e.target.value)}
                        placeholder='Gambar Olahraga'
                    />
                </div>
            </div>
            <div className='field'>
                <div className='control'>
                    <button type='submit' className='department__add-button'>
                        Tambah
                    </button>
                </div>
            </div>
        </form>
    </div>
  )
}

export default SportDataAdd
