import React, { useState, useEffect } from 'react'
import Slider from 'react-slick'

import './kompetisiOlahraga.css'
import 'slick-carousel/slick/slick.css'
import 'slick-carousel/slick/slick-theme.css'

import { List } from '../../components/index'
import { tes1, tes2, tes3, tes4, tes5, tes6, tes7, tes8, tes9} from '../../assets/tes/imports'
import {
  atletik,
  badminton,
  basket,
  catur,
  e_sport,
  futsal,
  renang,
  sepak_bola,
  tenis_lapangan,
  tenis_meja,
  voli
} from '../../assets/kompetisi/olahraga/imports'

const olahragaData = [
  {
    olahragaImage: atletik,
    olahragaName: 'Atletik'
  },
  {
    olahragaImage: badminton,
    olahragaName: 'Badminton'
  },
  {
    olahragaImage: basket,
    olahragaName: 'Basket'
  },
  {
    olahragaImage: catur,
    olahragaName: 'Catur'
  },
  {
    olahragaImage: e_sport,
    olahragaName: 'ESport'
  },
  {
    olahragaImage: futsal,
    olahragaName: 'Futsal'
  },
  {
    olahragaImage: renang,
    olahragaName: 'Renang'
  },
  {
    olahragaImage: sepak_bola,
    olahragaName: 'Sepak Bola'
  },
  {
    olahragaImage: tenis_lapangan,
    olahragaName: 'Tenis Lapangan'
  },
  {
    olahragaImage: tenis_meja,
    olahragaName: 'Tenis Meja'
  },
  {
    olahragaImage: voli,
    olahragaName: 'Tenis Lapangan'
  }
]

const KompetisiOlahraga = ({ getListTag }) => {

  const [listTag, setListTag] = useState('')

  useEffect(() => {
    getListTag(listTag)
  }, [getListTag, listTag])

  let settings = {
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 3,
    cssEase: 'linear'
  }

  return (
    <div className='spirit__content section__padding' id='olahraga'>
        <div className='spirit__heading'>
            <h1>OLAHRAGA</h1>
        </div>
        <div className='spirit__kompetisi-container'>
          <Slider {...settings}>
            {olahragaData.map((item, index) => (
              <List 
                listImage={item.olahragaImage} 
                listName={item.olahragaName} 
                listPropTag={setListTag}
                key={item.olahragaName}
              />
            ))}
          </Slider>
        </div>
    </div>
  )
}

export default KompetisiOlahraga
