import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import {CourseGoalList} from '../index'
import './matchComment.css'

import {CourseInput} from '../index'

const MatchComment = ({props}) => {
    const params = useParams()

    const [comments, setComments] = useState([]);

    useEffect(() => {
        getComments();
    }, []);

    const getComments = async () => {
        const response = await axios.get(`http://localhost:8000/api/matchs/${params.id}`)
        setComments(response.data);
    };

    const addGoalHandler = (enteredText) => {
        setComments((prevGoals) => {
        const updatedGoals = [...prevGoals];
        updatedGoals.unshift({ text: enteredText, id: Math.random().toString() });
        return updatedGoals;
        });
    };

    let content = (
        <p style={{ textAlign: "center" }}>No goals found. Maybe add one?</p>
    );

    if (comments.length > 0) {
        content = <CourseGoalList items={comments} />;
    }


    return (
        <div>
        <section id="comment-form">
            <CourseInput onAddGoal={addGoalHandler} props={props} />
        </section>
        <section id="comments">{content}</section>
        </div>
    )
}

export default MatchComment
